import re


KEY = re.compile(r' * @([a-zA-Z]+):(.+)', re.S)


def extract_doc(doc: str) -> dict:
    """ Extract header generated by 'korofildheader'
    """
    doc_dict = {key: line for key, line in KEY.findall(doc)}

    return doc_dict
